@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;700;900&family=Rajdhani:wght@300;400;500;600;700&display=swap');

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Rajdhani', sans-serif;
  background: #000;
  color: #fff;
  overflow-x: hidden;
}

.App {
  position: relative;
  min-height: 100vh;
  overflow: hidden;
}

.app-content {
  position: relative;
  z-index: 10;
  min-height: 100vh;
}

/* Home Screen Styles */
